import 'bootstrap/dist/css/bootstrap.css';
import Formulario from './components/Formulario';
import TablaContenido from './components/TablaContenido';
import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import { Admin, Analytics, Dashboard, Home, } from "./pages";
import Landing from "./pages/Landing";
import ProtectedRoute from "./components/ProtectedRoute";
import About from "./components/About";
import './App.css'
import FormLogin from "./pages/FormLogin";
import FormRegister from "./pages/FormRegister";
import User from "./components/User";
import Navigation from "./components/Navigation";


const App = () => {
    const [usuario, setUsuario] = useState([]);
    const mostrarUsuario = async () => {
        const response = await fetch('api/usuario/Lista');
        if (response.ok) {
            const data = await response.json();
            console.log(data);
            setUsuario(data);
        }
        else {
            console.log(`error de lista ${response.status}`)
        }
    }
    useEffect(() => {
        mostrarUsuario()
    }, [])

    const guardarDatos = async (usuario) => {
        const response = await fetch("api/usuario/Guardar", {
            method: "POST",
            headers: { 'Context-Type': 'application/json;charset=utf-8' },
            body: JSON.stringify(usuario)
        })
        if (response.ok) {
            mostrarUsuario();
        }
    }

    const [user, setUser] = useState(null);

    const login = () =>
        setUser({
            form: {

            }
        });
    const logout = () => setUser(null);



    return(
        <div>
            <BrowserRouter>
                <Navigation />


                <Routes>
                    <Route index element={<Landing />} />
                    <Route path="/landing" element={<Landing />} />
                    <Route element={<ProtectedRoute isAllowed={!!user} />}>
                        <Route path="/home" element={<Home />} />
                        <Route path="/dashboard" element={<Dashboard />} />
                    </Route>
                    <Route path="/form" element={<FormLogin />} />
                    <Route path="/about" element={<About />} />
                    <Route path="/formr" element={<FormRegister />} />
                    <Route path="/user" element={<User />} />

                    <Route
                        path="/analytics"
                        element={
                            <ProtectedRoute
                                redirectTo="/home"
                                isAllowed={!!user && user.permissions.includes("analize")}
                            >
                                <Analytics />
                            </ProtectedRoute>
                        }
                    />
                    <Route
                        path="/admin"
                        element={
                            <ProtectedRoute
                                redirectTo="/home"
                                isAllowed={!!user && user.roles.includes("admin")}
                            >
                                <Admin />
                            </ProtectedRoute>
                        }
                    />
                </Routes>
            </BrowserRouter>
            
            </div>
    )      
}
export default App;
